import React, { useState } from 'react';
import { Button, TextField, Typography, Paper } from '@material-ui/core';
import FileBase from 'react-file-base64';
import { useDispatch } from 'react-redux';
import { createPost } from '../../actions/posts'
import useStyles from './styles.js';

const Form = () => {
    const [postData, setPostData] = useState({
        creator: '',
        location: '',
        comment: '',
        buddies: [],
        tags: [],
        maxDepth: '',
        duration: '',
        selectedFile: '' 
    });
    const classes = useStyles();
    const dispatch = useDispatch();
    
    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(createPost(postData));
    }

    const clear = () => {

    }
    
    return (
        <Paper className={classes.paper}>
            <form autoComplete='off' 
            noValidate 
            className={`${classes.form}`} 
            onSubmit={handleSubmit}>
                <Typography variant='h4'>Create a new Log!</Typography>
                <TextField 
                className={classes.input}
                name='creator' 
                variant='outlined' 
                label='Creator' 
                fullWidth
                value={postData.creator}
                onChange={(e) => setPostData({
                    ...postData, creator: e.target.value
                })}/>
                <TextField 
                className={classes.input}
                name='location' 
                variant='outlined' 
                label='Location' 
                fullWidth
                value={postData.location}
                onChange={(e) => setPostData({
                    ...postData, location: e.target.value
                })}/>
                <TextField 
                className={classes.input}
                name='buddies' 
                variant='outlined' 
                label='Buddies' 
                fullWidth
                value={postData.buddies}
                onChange={(e) => setPostData({
                    ...postData, buddies: e.target.value
                })}/>
                <TextField 
                className={classes.input}
                name='comment' 
                variant='outlined' 
                label='Comment' 
                fullWidth
                value={postData.comment}
                onChange={(e) => setPostData({
                    ...postData, comment: e.target.value
                })}/>
                <TextField 
                className={classes.input}
                name='maxDepth' 
                variant='outlined' 
                label='Maximum Depth' 
                fullWidth
                value={postData.maxDepth}
                onChange={(e) => setPostData({
                    ...postData, maxDepth: e.target.value
                })}/>
                <TextField 
                className={classes.input}
                name='duration' 
                variant='outlined' 
                label='Duration' 
                fullWidth
                value={postData.duration}
                onChange={(e) => setPostData({
                    ...postData, duration: e.target.value
                })}/>
                <TextField 
                className={classes.input}
                name='tags' 
                variant='outlined' 
                label='Tags' 
                fullWidth
                value={postData.tags}
                onChange={(e) => setPostData({
                    ...postData, tags: e.target.value
                })}/>
                <div className={classes.fileInput}>
                    <FileBase
                        type='file'
                        multiple={false}
                        onDone={(base64) => setPostData( { ...postData, selectedFile: base64 })}
                    />
                </div>  
                <Button className={classes.buttonSubmit} variant='contained' color='primary' size='large' type='submit' fullWidth>Submit</Button>
                <Button className={classes.buttonClear} variant='contained' color='secondary' size='small' onClick={clear} fullWidth>Clear</Button>
            </form>
        </Paper>
    )
}

export default Form;
