import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import moment from 'moment';

import useStyles from './styles.js';

const Post = ({ post }) => {
    const classes = useStyles();
    
    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={post.selectedFile} title={post.title}>
                <div className={classes.overlay}>
                    <Typography variant='h4'>{post.creator}</Typography>
                    <Typography variant='h6'>Location: {post.location}</Typography>
                    <Typography variant='body2'>Notes: {post.comment}</Typography>
                    <Typography variant='body2'>Maximum Depth: {post.maxDepth}</Typography>
                    <Typography variant='body2'>Dive Duration: {post.duration}</Typography>
                    <Typography variant='body2'>Dive Logged: {moment(post.createdAt).fromNow()}</Typography>
                </div>
            </CardMedia>
        </Card>
    )
} 

export default Post;
