const requests = (posts = [], action) => {
    switch(action.type) {
        case 'UPDATE':
            return posts.map((post) => post.id === action.payload._id ? action.payload : post);
        case 'FETCH_ALL': 
            return action.payload;
        case 'CREATE':
            return [ ...posts, action.payload ];
        default:
            return posts;
    }
}

export default requests;
