import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import moment from 'moment';

import useStyles from './styles.js';

const Post = ({ post, setCurrentId }) => {
    const classes = useStyles();
    
    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={post.selectedFile} title={post.title}>
            </CardMedia>
            <div className={classes.overlay}>
                <div className={classes.overlay2}>
                    <Typography variant='h4'>{post.creator}</Typography>
                    <Button style={{color: 'black'}} size='small' onClick={()=> setCurrentId(post._id) }>
                        <MoreVertIcon fontSize='medium' />
                    </Button>
                </div>
                { post.diveNumber && <Typography variant='h6'>Dive #{post.diveNumber}</Typography> }
                <Typography variant='h6'>Location: {post.location}</Typography>
                <Typography variant='body2'>Maximum Depth: {post.maxDepth}</Typography>
                <Typography variant='body2'>Dive Duration: {post.duration}</Typography>
                { post.buddies != false && <Typography variant='body2'>Buddies: {post.buddy}</Typography> }
                { post.notes && <Typography variant='body2'>Notes: {post.notes}</Typography> }
                <Typography variant='body2'>Dive Logged: {moment(post.createdAt).fromNow()}</Typography>
            </div>
            <div className={classes.details}>
                { post.tags != false && <Typography variant='body2' color='textSecondary'>{post.tags.map((tag) => `#${tag} `)}</Typography> }
            </div>
            <CardActions className={classes.cardActions}>
                <Button size='small' color='primary' onclick={() => {}} >
                    <ThumbUpAltIcon fontSize='small' />
                    Likes: {post.likeCount}
                </Button>
                <Button size='small' color='primary' onclick={() => {}} >
                    <DeleteForeverIcon fontSize='small' />
                    Delete
                </Button>
            </CardActions>
        </Card>
    )
} 

export default Post;
